var React = require('react');
var ReactDOM = require('react-dom');
const addButonText = '+';
const subtractButtonText = '-';
const rootElement = document.getElementById("root");

class NumberChanger extends React.Component {

    state = { counter: 0 };

    addOne = () => {
        this.setState((prevState) => ({
            counter: prevState.counter + 1
        }));
    };

    subtractOne = () => {
        this.setState((prevState) => ({
            counter: prevState.counter - 1
        }));
    };

    render() {
        return ([
            <button onClick={this.addOne}>
                {addButonText}
            </button>,
            <button onClick={this.subtractOne}>
                {subtractButtonText}
            </button>
        ]
        );
    }

}

var Result = (props) => {
    return (
        <div>{this.props}</div>
    );
};

class App extends React.Component {
    render() {
        return (
            <div>
                <NumberChanger />
                <Result />
            </div>
        );
    }
}

ReactDOM.render(<App />, rootElement);
